class ImageService {
  constructor() {}

  // Fetch image data by ID
  async getImageById(id) {
    const response = await fetch(`/api/images/${id}`);
    if (!response.ok) throw new Error('Failed to fetch image');
    return await response.json();
  }

  // Update image with given fields
  async updateImage(id, fields) {
    const response = await fetch(`/api/images/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(fields),
    });
    if (!response.ok) throw new Error('Failed to update image');
    return await response.json();
  }

  // Delete text comment by index
  async deleteTextComment(id, idx) {
    const image = await this.getImageById(id);
    if (image.textComments && image.textComments.length > idx) {
      image.textComments.splice(idx, 1);
      return await this.updateImage(id, { textComments: image.textComments });
    }
    return image;
  }

  // Delete audio comment by index
  async deleteAudioComment(id, idx) {
    const image = await this.getImageById(id);
    if (image.audioComments && image.audioComments.length > idx) {
      image.audioComments.splice(idx, 1);
      return await this.updateImage(id, { audioComments: image.audioComments });
    }
    return image;
  }

  // Delete drawing
  async deleteDrawing(id) {
    return await this.updateImage(id, { drawing: null });
  }

  // Delete entire image
  async deleteImage(id) {
    const response = await fetch(`/api/images/${id}`, {
      method: 'DELETE',
    });
    if (!response.ok) throw new Error('Failed to delete image');
  }
}

export default ImageService;
