import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ImageDetail from '../components/ImageDetail';
import { getImageById, updateImage } from '../services/storage';

function ImageView() {
  const { id } = useParams();
  const [image, setImage] = useState(null);

  useEffect(() => {
    const loadImage = async () => {
      const loadedImage = await getImageById(id);
      setImage(loadedImage);
    };
    loadImage();
  }, [id]);

  const handleDrawingSave = async (imageId, drawingUrl) => {
    const updatedImage = await updateImage(imageId, { drawing: drawingUrl });
    setImage(updatedImage);
  };

  const handleAudioCommentSave = async (imageId, audioUrl) => {
    const updatedImage = await updateImage(imageId, { 
      audioComments: [...(image.audioComments || []), audioUrl]
    });
    setImage(updatedImage);
  };

  if (!image) {
    return <div>Loading...</div>;
  }

  return (
    <div className="image-view">
      <ImageDetail 
        image={image} 
        onDrawingSave={handleDrawingSave}
        onAudioCommentSave={handleAudioCommentSave}
      />
    </div>
  );
}

export default ImageView;
