class ImageStorage {
  constructor() {
    this.STORAGE_KEY = 'image_gallery';
    this.initialize();
  }

  initialize() {
    if (!localStorage.getItem(this.STORAGE_KEY)) {
      localStorage.setItem(this.STORAGE_KEY, JSON.stringify([]));
    }
  }

  async getImages() {
    return JSON.parse(localStorage.getItem(this.STORAGE_KEY));
  }

  async getImageById(id) {
    const images = await this.getImages();
    return images.find(img => img.id === id);
  }

  async saveImage(imageData) {
  const MAX_IMAGES = 20;
  const images = await this.getImages();
  const newImage = {
    id: Date.now().toString(),
    ...imageData
  };
  const updatedImages = [newImage, ...images];

  // Ограничиваем количество изображений
  if (updatedImages.length > MAX_IMAGES) {
    updatedImages.splice(MAX_IMAGES); // Удаляем все после 20-го
  }

  localStorage.setItem(this.STORAGE_KEY, JSON.stringify(updatedImages));
  return newImage;
}


  async updateImage(id, updates) {
    const images = await this.getImages();
    const index = images.findIndex(img => img.id === id);
    if (index === -1) return null;
    
    const updatedImage = {
      ...images[index],
      ...updates
    };
    
    const updatedImages = [
      ...images.slice(0, index),
      updatedImage,
      ...images.slice(index + 1)
    ];
    
    localStorage.setItem(this.STORAGE_KEY, JSON.stringify(updatedImages));
    return updatedImage;
  }
    async deleteImage(id) {
    const images = await this.getImages();
    const updatedImages = images.filter(img => img.id !== id);
    localStorage.setItem(this.STORAGE_KEY, JSON.stringify(updatedImages));
  }

  async deleteTextComment(imageId, commentIndex) {
    const image = await this.getImageById(imageId);
    if (!image || !image.textComments) return null;
    image.textComments.splice(commentIndex, 1);
    return this.updateImage(imageId, { textComments: [...image.textComments] });
  }

  async deleteAudioComment(imageId, commentIndex) {
    const image = await this.getImageById(imageId);
    if (!image || !image.audioComments) return null;
    image.audioComments.splice(commentIndex, 1);
    return this.updateImage(imageId, { audioComments: [...image.audioComments] });
  }
  async deleteDrawing(imageId) {
    const image = await this.getImageById(imageId);
    if (!image || !image.drawing) return null;
  return this.updateImage(imageId, { drawing: null });
}
}

export const storage = new ImageStorage();

// Helper functions
export const getImages = () => storage.getImages();
export const getImageById = (id) => storage.getImageById(id);
export const saveImage = (imageData) => storage.saveImage(imageData);
export const updateImage = (id, updates) => storage.updateImage(id, updates);
export const deleteImage = (id) => storage.deleteImage(id);
export const deleteTextComment = (id, idx) => storage.deleteTextComment(id, idx);
export const deleteAudioComment = (id, idx) => storage.deleteAudioComment(id, idx);
export const deleteDrawing = (id) => storage.deleteDrawing(id);
