export class Comment {
  constructor(id, createdAt = new Date()) {
    // Unique identifier for each comment
    this.id = id || Math.random().toString(36).substr(2, 9);
    // Timestamp when the comment was created
    this.createdAt = createdAt;
  }
}

// TextComment extends Comment, adds a text field
export class TextComment extends Comment {
  constructor(text, id, createdAt) {
    super(id, createdAt);
    this.text = text;
  }
}

// AudioComment extends Comment, adds an audio URL field
export class AudioComment extends Comment {
  constructor(audioUrl, id, createdAt) {
    super(id, createdAt);
    this.audioUrl = audioUrl;
  }
}
