import React, { useState, useRef } from 'react';

function ImageUploader({ onUpload }) {
  const [image, setImage] = useState(null);
  const [description, setDescription] = useState('');
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type.match('image.*')) {
      setFileName(file.name);
      const reader = new FileReader();
      reader.onload = (event) => {
        setImage(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (image && description) {
      const newImage = {
        id: Date.now().toString(),
        url: image,
        description,
      };

      const storedImages = JSON.parse(localStorage.getItem('image_gallery')) || [];
      if (!storedImages.some(img => img.id === newImage.id)) {
        storedImages.push(newImage);
        localStorage.setItem('image_gallery', JSON.stringify(storedImages));
      }

      onUpload?.(newImage);
      setImage(null);
      setDescription('');
      setFileName('');
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="uploader">
      <h2>Upload Image</h2>
      <form onSubmit={handleSubmit}>
        <label className="file-upload-label">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
          </svg>
          <span>Click to select an image</span>
          {fileName && <span className="file-name">{fileName}</span>}
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            ref={fileInputRef}
            required
          />
        </label>

        {image && (
          <div className="image-preview">
            <img src={image} alt="Preview" />
          </div>
        )}

        <textarea
          placeholder="Enter image description..."
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        <button type="submit" disabled={!image || !description}>
          <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
            <polyline points="17 8 12 3 7 8"></polyline>
            <line x1="12" y1="3" x2="12" y2="15"></line>
          </svg>
          Upload Image
        </button>
      </form>
    </div>
  );
}

export default ImageUploader;
