import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getImages } from '../services/storage';

function RecentImages() {
  const [recentImages, setRecentImages] = useState([]);

  useEffect(() => {
    const fetchImages = async () => {
      const images = await getImages();
      if (images && images.length > 0) {
        setRecentImages(images.slice(-4).reverse());
      }
    };
    fetchImages();
  }, []);

  return (
    <section className="recent-images">
      <h2>Recent images</h2>
      <div className="image-grid">
        {recentImages.length === 0 ? (
          <p>No recent images.</p>
        ) : (
          recentImages.map(image => (
            <Link to={`/image/${image.id}`} key={image.id}>
              <img src={image.url} alt={image.description} className="thumb" />
            </Link>
          ))
        )}
      </div>
    </section>
  );
}

export default RecentImages;
