import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ImageDetail from '../components/ImageDetail';
import TextComment from '../components/TextComment';
import { getImageById, updateImage } from '../services/storage';
import { deleteTextComment, deleteAudioComment, deleteImage } from '../services/storage';
import { deleteDrawing } from '../services/storage';

function ImageView() {
  const { id } = useParams();
  const [image, setImage] = useState(null);

  useEffect(() => {
    const loadImage = async () => {
      const loadedImage = await getImageById(id);
      setImage(loadedImage);
    };
    loadImage();
  }, [id]);

  const handleDrawingSave = async (imageId, drawingUrl) => {
    const updatedImage = await updateImage(imageId, { drawing: drawingUrl });
    setImage(updatedImage);
  };

  const handleAudioCommentSave = async (imageId, audioUrl) => {
    const updatedImage = await updateImage(imageId, {
      audioComments: [...(image.audioComments || []), audioUrl],
    });
    setImage(updatedImage);
  };

  const handleTextCommentSave = async (imageId, commentText) => {
    const updatedImage = await updateImage(imageId, {
      textComments: [...(image.textComments || []), commentText],
    });
    setImage(updatedImage);
  };
  const handleDeleteTextComment = async (idx) => {
  const updatedImage = await deleteTextComment(image.id, idx);
  setImage(updatedImage);
};

const handleDeleteAudioComment = async (idx) => {
  const updatedImage = await deleteAudioComment(image.id, idx);
  setImage(updatedImage);
};

const handleDeleteImage = async () => {
  await deleteImage(image.id);
  // например, редирект обратно
  window.location.href = '/';
};

  if (!image) {
    return <div>Loading...</div>;
  }

  return (
    <div className="image-view">
  <button onClick={handleDeleteImage}>Delete Image</button>

  <ImageDetail 
    image={image} 
    onDrawingSave={handleDrawingSave}
    onAudioCommentSave={handleAudioCommentSave}
  />

  {image.audioComments && image.audioComments.length > 0 && (
    <div className="audio-comments">
      <h3>Audio Comments</h3>
      <ul>
        {image.audioComments.map((url, idx) => (
          <li key={idx}>
            <audio controls src={url} />
            <button onClick={() => handleDeleteAudioComment(idx)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  )}

  {image.textComments && image.textComments.length > 0 && (
    <div className="text-comments">
      <h3>Text Comments</h3>
      <ul>
        {image.textComments.map((comment, idx) => (
          <li key={idx}>
            {comment}
            <button onClick={() => handleDeleteTextComment(idx)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  )}

  <TextComment onSave={(text) => handleTextCommentSave(image.id, text)} />
</div>
  );
}

export default ImageView;
