import React, { useEffect, useRef, useState } from 'react';

function DrawingCanvas({ imageSrc, onSave }) {
  const backgroundCanvasRef = useRef(null);
  const drawingCanvasRef = useRef(null);
  const containerRef = useRef(null);

  const [isDrawing, setIsDrawing] = useState(false);
  const [color, setColor] = useState('#000000');
  const [brushSize, setBrushSize] = useState(5);
  const [isEraser, setIsEraser] = useState(false);
  const [scale, setScale] = useState(1);
  const [offset, setOffset] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const bgCanvas = backgroundCanvasRef.current;
    const drawCanvas = drawingCanvasRef.current;
    const container = containerRef.current;
    const ctx = bgCanvas.getContext('2d');
    const img = new Image();

    img.onload = () => {
      // Получаем размеры контейнера
      const containerWidth = container.offsetWidth;
      const containerHeight = Math.floor(containerWidth * 0.75); // Соотношение 4:3

      // Устанавливаем размеры канвасов
      bgCanvas.width = containerWidth;
      bgCanvas.height = containerHeight;
      drawCanvas.width = containerWidth;
      drawCanvas.height = containerHeight;

      // Рассчитываем масштаб с сохранением пропорций
      const scaleRatio = Math.min(
        containerWidth / img.width,
        containerHeight / img.height
      );
      setScale(scaleRatio);

      // Рассчитываем отступы для центрирования
      const offsetX = (containerWidth - img.width * scaleRatio) / 2;
      const offsetY = (containerHeight - img.height * scaleRatio) / 2;
      setOffset({ x: offsetX, y: offsetY });

      // Очищаем и рисуем изображение
      ctx.clearRect(0, 0, containerWidth, containerHeight);
      ctx.drawImage(
        img,
        0,
        0,
        img.width,
        img.height,
        offsetX,
        offsetY,
        img.width * scaleRatio,
        img.height * scaleRatio
      );
    };

    img.src = imageSrc;
  }, [imageSrc]);

  const getCanvasPosition = (clientX, clientY) => {
  const canvas = drawingCanvasRef.current;
  const rect = canvas.getBoundingClientRect();

  // Убираем offset, т.к. рисуем на весь канвас, а не смещённо
  const x = (clientX - rect.left) / scale;
  const y = (clientY - rect.top) / scale;

  return { x, y };
};

  const startDrawing = (e) => {
    const canvas = drawingCanvasRef.current;
    const ctx = canvas.getContext('2d');
    const { x, y } = getCanvasPosition(
      e.clientX || e.touches[0].clientX,
      e.clientY || e.touches[0].clientY
    );

    ctx.beginPath();
    ctx.moveTo(x, y);
    ctx.lineWidth = brushSize;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';
    ctx.globalCompositeOperation = isEraser ? 'destination-out' : 'source-over';
    ctx.strokeStyle = isEraser ? 'rgba(0,0,0,1)' : color;

    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing) return;

    const canvas = drawingCanvasRef.current;
    const ctx = canvas.getContext('2d');
    const { x, y } = getCanvasPosition(
      e.clientX || e.touches[0].clientX,
      e.clientY || e.touches[0].clientY
    );

    ctx.lineTo(x, y);
    ctx.stroke();
    ctx.beginPath();
    ctx.moveTo(x, y);
  };

  const stopDrawing = () => {
    setIsDrawing(false);
    const ctx = drawingCanvasRef.current.getContext('2d');
    ctx.beginPath();
  };

  const handleSave = () => {
    const bgCanvas = backgroundCanvasRef.current;
    const drawingCanvas = drawingCanvasRef.current;
    const finalCanvas = document.createElement('canvas');
    
    // Используем оригинальные размеры изображения
    const img = new Image();
    img.onload = () => {
      finalCanvas.width = img.width;
      finalCanvas.height = img.height;
      
      const ctx = finalCanvas.getContext('2d');
      
      // Рисуем фоновое изображение
      ctx.drawImage(img, 0, 0, img.width, img.height);
      
      // Рисуем рисунок с правильным масштабированием
      ctx.drawImage(
        drawingCanvas,
        offset.x, offset.y,
        img.width * scale, img.height * scale,
        0, 0,
        img.width, img.height
      );
      
      const dataUrl = finalCanvas.toDataURL('image/png');
      onSave(dataUrl);
    };
    img.src = imageSrc;
  };

  const resetDrawing = () => {
    const ctx = drawingCanvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, drawingCanvasRef.current.width, drawingCanvasRef.current.height);
  };

  return (
    <div className="drawing-canvas" ref={containerRef}>
      <canvas
        ref={backgroundCanvasRef}
        style={{ position: 'absolute', top: 0, left: 0 }}
      />
      <canvas
        ref={drawingCanvasRef}
        style={{ position: 'absolute', top: 0, left: 0 }}
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseLeave={stopDrawing}
        onTouchStart={startDrawing}
        onTouchMove={draw}
        onTouchEnd={stopDrawing}
      />
      <div className="drawing-controls">
        <label>
          Цвет:
          <input
            type="color"
            value={color}
            onChange={(e) => setColor(e.target.value)}
            disabled={isEraser}
          />
        </label>
        <label>
          Размер кисти:
          <input
            type="range"
            min="1"
            max="50"
            value={brushSize}
            onChange={(e) => setBrushSize(+e.target.value)}
          />
        </label>
        <label>
          <input
            type="checkbox"
            checked={isEraser}
            onChange={() => setIsEraser(!isEraser)}
          />
          Ластик
        </label>
        <button onClick={resetDrawing}>Сбросить</button>
        <button onClick={handleSave}>Сохранить</button>
      </div>
    </div>
  );
}

export default DrawingCanvas;
