import React, { useEffect, useRef, useState } from 'react';

function DrawingCanvas({ imageSrc, onSave }) {
  const backgroundCanvasRef = useRef(null);
  const drawingCanvasRef = useRef(null);

  const [isDrawing, setIsDrawing] = useState(false);
  const [color, setColor] = useState('#000000');
  const [brushSize, setBrushSize] = useState(5);
  const [isEraser, setIsEraser] = useState(false);

 


  useEffect(() => {
  const bgCanvas = backgroundCanvasRef.current;
  const drawCanvas = drawingCanvasRef.current;
  const ctx = bgCanvas.getContext('2d');
  const img = new Image();

  img.onload = () => {
    // Фиксированные размеры для канвасов
    const canvasWidth = 400;
    const canvasHeight = 300;

    // Устанавливаем размеры канвасов в пикселях
    bgCanvas.width = canvasWidth;
    bgCanvas.height = canvasHeight;
    drawCanvas.width = canvasWidth;
    drawCanvas.height = canvasHeight;

    // Масштабируем изображение с сохранением пропорций
    const ratio = Math.min(canvasWidth / img.width, canvasHeight / img.height);
    const width = img.width * ratio;
    const height = img.height * ratio;
    const x = (canvasWidth - width) / 2;
    const y = (canvasHeight - height) / 2;

    // Чистим фон и рисуем изображение
    ctx.clearRect(0, 0, canvasWidth, canvasHeight);
    ctx.drawImage(img, x, y, width, height);
  };

  img.src = imageSrc;
}, [imageSrc]);

  const getMousePos = (e) => {
    const rect = drawingCanvasRef.current.getBoundingClientRect();
    return {
      x: e.clientX - rect.left,
      y: e.clientY - rect.top,
    };
  };

  const startDrawing = (e) => {
    const ctx = drawingCanvasRef.current.getContext('2d');
    const { x, y } = getMousePos(e);

    ctx.beginPath();
    ctx.moveTo(x, y);
    ctx.lineWidth = brushSize;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';

    ctx.globalCompositeOperation = isEraser ? 'destination-out' : 'source-over';
    ctx.strokeStyle = color;

    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing) return;

    const ctx = drawingCanvasRef.current.getContext('2d');
    const { x, y } = getMousePos(e);

    ctx.lineTo(x, y);
    ctx.stroke();
  };

  const stopDrawing = () => {
    setIsDrawing(false);
  };

  const handleSave = () => {
    const bgCanvas = backgroundCanvasRef.current;
    const drawingCanvas = drawingCanvasRef.current;

    // Создаём временный canvas
    const finalCanvas = document.createElement('canvas');
    finalCanvas.width = bgCanvas.width;
    finalCanvas.height = bgCanvas.height;

    const ctx = finalCanvas.getContext('2d');
    ctx.drawImage(bgCanvas, 0, 0);
    ctx.drawImage(drawingCanvas, 0, 0);

    const dataUrl = finalCanvas.toDataURL('image/png');
    onSave(dataUrl);
  };

  const resetDrawing = () => {
    const ctx = drawingCanvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, drawingCanvasRef.current.width, drawingCanvasRef.current.height);
  };

  return (
    <div className="drawing-canvas" >
      <canvas ref={backgroundCanvasRef} />
      <canvas
        ref={drawingCanvasRef}
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseLeave={stopDrawing}
      />
      <div className="drawing-controls" style={{ marginTop: 'calc(100% + 10px)' }}>
        <label>
          Color:
          <input type="color" value={color} onChange={(e) => setColor(e.target.value)} disabled={isEraser} />
        </label>
        <label>
          Brush Size:
          <input type="range" min="1" max="50" value={brushSize} onChange={(e) => setBrushSize(+e.target.value)} />
        </label>
        <label>
          <input type="checkbox" checked={isEraser} onChange={() => setIsEraser(!isEraser)} />
          Eraser
        </label>
        <button onClick={resetDrawing}>Reset</button>
        <button onClick={handleSave}>Save Drawing</button>
      </div>
    </div>
  );
}

export default DrawingCanvas;
