import React, { useState, useRef } from 'react';

function ImageUploader({ onUpload }) {
  const [image, setImage] = useState(null);
  const [description, setDescription] = useState('');
  const fileInputRef = useRef(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type.match('image.*')) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setImage(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (image && description) {
      const newImage = {
        id: Date.now().toString(), // строка id
        url: image,
        description,
      };

      // Сохраняем в localStorage с ключом 'image_gallery'
      const storedImages = JSON.parse(localStorage.getItem('image_gallery')) || [];
      storedImages.push(newImage);
      localStorage.setItem('image_gallery', JSON.stringify(storedImages));

      onUpload?.(newImage);

      setImage(null);
      setDescription('');
      fileInputRef.current.value = '';
    }
  };


  return (
    <div className="uploader">
      <h2>Upload Image</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            ref={fileInputRef}
            required
          />
        </div>
        <div>
          <textarea
            placeholder="Image description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <button type="submit">Upload</button>
      </form>
    </div>
  );
}

export default ImageUploader;
