import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const [recentImages, setRecentImages] = useState([]);

  useEffect(() => {
    // Получаем изображения из localStorage
    const stored = localStorage.getItem('images');
    if (stored) {
      const images = JSON.parse(stored);
      // Показываем только последние 4
      setRecentImages(images.slice(-4).reverse());
    }
  }, []);

  return (
    <div className="home">
      <header>
        <h1>Welcome to Image Gallery</h1>
        <p>Upload, view, and comment on images with audio and drawings</p>
      </header>

      <nav>
        <Link to="/gallery" className="button">View Gallery</Link>
      </nav>

      <section className="recent-images">
        <h2>Recent Images</h2>
        <div className="image-grid">
          {recentImages.length === 0 ? (
            <p>No recent images.</p>
          ) : (
            recentImages.map(image => (
              <Link to={`/image/${image.id}`} key={image.id}>
                <img src={image.url} alt={image.title} className="thumb" />
              </Link>
            ))
          )}
        </div>
      </section>
    </div>
  );
}

export default Home;
