import React from 'react';
import { Link } from 'react-router-dom';

function Home() {
  return (
    <div className="home">
      <header>
        <h1>Welcome to Image Gallery</h1>
        <p>Upload, view, and comment on images with audio and drawings</p>
      </header>
      <nav>
        <Link to="/gallery" className="button">View Gallery</Link>
      </nav>
      <section className="features">
        <h2>Features</h2>
        <ul>
          <li>Upload images with descriptions</li>
          <li>Draw on images and save your artwork</li>
          <li>Add audio comments to images</li>
          <li>Works offline with cached content</li>
          <li>View history of recently seen images</li>
        </ul>
      </section>
    </div>
  );
}

export default Home;
