import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getImages } from '../services/storage';

function Offline() {
  const [offlineImages, setOfflineImages] = useState([]);

  useEffect(() => {
    const loadOfflineContent = async () => {
      const images = await getImages();
      setOfflineImages(images);
    };
    loadOfflineContent();
  }, []);

  return (
    <div className="offline-page">
      <h1>Offline Mode</h1>
      <p>You're currently offline. Here's the content you can access:</p>
      
      <div className="offline-content">
        <h2>Your Gallery</h2>
        {offlineImages.length > 0 ? (
          <div className="image-grid">
            {offlineImages.map((image) => (
              <div key={image.id} className="image-card">
                <Link to={`/image/${image.id}`}>
                  <img src={image.url} alt={image.description} />
                </Link>
                <p>{image.description}</p>
              </div>
            ))}
          </div>
        ) : (
          <p>No images available offline</p>
        )}
      </div>
      
      <div className="offline-actions">
        <Link to="/" className="button">Back to Home</Link>
      </div>
    </div>
  );
}

export default Offline;
