// Base Comment class
export class Comment {
  constructor(id, createdAt = new Date()) {
    // Unique identifier for each comment
    this.id = id || Math.random().toString(36).substr(2, 9);
    // Timestamp when the comment was created
    this.createdAt = createdAt;
  }
}
