import React, { useState, useEffect } from 'react';
import DrawingCanvas from './DrawingCanvas';
import AudioComment from './AudioComment';

function ImageDetail({ image, onDrawingSave, onAudioCommentSave }) {
  const [viewHistory, setViewHistory] = useState([]);

  useEffect(() => {
    // Add to view history
    const history = JSON.parse(localStorage.getItem('viewHistory') || '[]');
    const newHistory = [image, ...history.filter(item => item.id !== image.id)].slice(0, 10);
    localStorage.setItem('viewHistory', JSON.stringify(newHistory));
    setViewHistory(newHistory);
  }, [image]);

  return (
    <div className="image-detail">
      <div className="image-container">
        <img src={image.url} alt={image.description} />
        <div className="image-meta">
          <h2>{image.description || 'Untitled'}</h2>
          <p>Uploaded: {new Date(image.timestamp).toLocaleString()}</p>
        </div>
      </div>
      
      <div className="image-tools">
        <DrawingCanvas 
          imageSrc={image.drawing || image.url} 
          onSave={(drawing) => onDrawingSave(image.id, drawing)} 
        />
        <AudioComment 
          onSave={(audioUrl) => onAudioCommentSave(image.id, audioUrl)} 
        />
        {image.audioComments && image.audioComments.length > 0 && (
          <div className="audio-comments">
            <h3>Saved Audio Comments</h3>
            {image.audioComments.map((url, idx) => (
              <audio key={idx} controls src={url} />
            ))}
          </div>
        )}
       
      </div>
      
      <div className="view-history">
        <h3>Recently Viewed</h3>
        <div className="history-grid">
          {viewHistory.map(item => (
            <div key={item.id} className="history-item">
              <img src={item.url} alt={item.description} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ImageDetail;
